const args = require ( 'args' );
 
const ws = require ( 'ws' );

const server = module .exports = new ws .Server ( {

host: args [ '--host' ] || 'localhost',
port: args [ '--port' ] || 1313

} );

server .passes = [];

[

'listening',
'connection'

] .forEach ( ( event ) => {

server .on ( event, require ( './' + event ) );

} );
